//REGISTRAR CORTE
function registrarCortes(){
	var datos = $("#corteRegistro").serialize();
		$.ajax({
			method:"POST",
			url: "ajax/registrarcAjax.php",
			data:datos,
			success:function(e) {
				
				if (e==1) {
					alert("REGISTRADO");
				}else{
					alert("ERROR");
				}
			}

		});
		
}


/* MOSTRAR CORTES*/
