function registrarPagina() {
    var datos = $("#paginaRegistro").serialize();
    $.ajax({
        method:"POST",
        url:"ajax/registrareAjax.php",
        data:datos,
        success:function(e) {
            if (e==1) {
                alert("Registro de Barberia Exitoso");
            }else{
                alert("No se pudo registrer Barberia");
            }
        }

    });
}